import React from 'react'
import { useState, useEffect } from 'react';
import axios from 'axios';
import {
    MDBContainer,MDBRow,MDBCol,MDBCard,MDBCardBody,MDBModalFooter,MDBIcon,MDBCardHeader,MDBBtn,MDBInput} from "mdbreact";
export const UserAuth = (props) => {
    const [ticketData, setTicketData] = useState(null);
    const [devloperId, inputDevId] = useState("");
    const [pass, inputPassword] = useState("");
    const handleInputDevId = (e) => {
        inputDevId(e.target.value);
    };
    const handleInputPass = (e) => {
        inputPassword(e.target.value);
    };
    // const callAPI  =()=>{
    //     setTimeout(callTheAPI, 1000);
    //     return 1;
    // }
    async function callAPI(){
        let res = await axios({
            url: 'http://localhost:8000/userAuth',
            method: 'post',
            timeout: 8000,
            headers: {
                'Content-Type': 'application/json',
            },
            data : { devId: devloperId,password:pass}
        })   
        // Don't forget to return something   
        return res
      }
    return (
        <>
        <MDBContainer>
        <MDBRow>
          <MDBCol md="6">
            <MDBCard>
              <MDBCardBody>
                <MDBCardHeader className="form-header deep-blue-gradient rounded">
                  <h3 className="my-3">
                    <MDBIcon icon="lock" /> Login:
                  </h3>
                </MDBCardHeader>
                <form>
                  <div className="grey-text">
                    <MDBInput
                      label="Type your email"
                      icon="envelope"
                      group
                      type="email"
                      validate
                      error="wrong"
                      success="right"
                    />
                    <MDBInput
                      label="Type your password"
                      icon="lock"
                      group
                      type="password"
                      validate
                    />
                  </div>

                <div className="text-center mt-4">
                  <MDBBtn
                    color="light-blue"
                    className="mb-3"
                    type="submit"
                  >
                    Login
                  </MDBBtn>
                </div>
                </form>
                <MDBModalFooter>
                  <div className="font-weight-light">
                    <p>Not a member? Sign Up</p>
                    <p>Forgot Password?</p>
                  </div>
                </MDBModalFooter>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer></>
    )
}
