import { useState, useEffect } from 'react';

export const GetAllProject = (props) => {
  const [projects, setAllProjects] = useState(null);

  // + adding the use
  useEffect(() => {
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },

    };
    getData();

    // we will use async/await to fetch this data
    async function getData() {
      const response = await fetch("http://localhost:8000/getProjectList", requestOptions);
      const apiResponseData = await response.json();
      //console.log(apiResponseData);//remove this after use
      setAllProjects(apiResponseData);
    }
  }, []);
  let style={
    marginLeft: "auto",
    marginRight: "auto",
    marginBottom: "20px",
    marginTop: "20px",
    width: "70%"
  }
  return (
    <>
    {props.bucketNameDropdown==="true"  && 
      <div>
      {projects && (
        <div className="col-md-4">
           <select id="inputBucket" className="form-select">
            {projects.map((individualProject, index) => (
              <option key={index}>{individualProject.bucket_name}</option>
            ))}
          </select>
        </div>
      )}
    </div>
    }  
    {props.bucketNameDropdown===true  && 
      <div>
      {projects && (
        <div className="col-md-4">
           <select id="inputBucket" className="form-select">
            {projects.map((individualProject, index) => (
              <option key={index}>{individualProject.bucket_name}</option>
            ))}
          </select>
        </div>
      )}
    </div>
    }  
    if(check if props has val){
       <div>
       {projects && (
         <div className="projects">
           <ul className="list-group list-group-flush">
             {projects.map((individualProject, index) => (

               <div  style={style} className="card text-center" key={index}>
                 <div className="card-body">
                     <p className="card-text">
                     {individualProject.bucket_name}
                     </p>

                 </div>
             </div>

             ))}
           </ul>

         </div>
       )}
     </div>
    }  
    </>
  )
}
