import React from 'react'
import { useState, useEffect } from 'react';
import axios from 'axios';
export const UserAuth = (props) => {
    const [ticketData, setTicketData] = useState(null);
    const [devloperId, inputDevId] = useState("");
    const [pass, inputPassword] = useState("");
    const handleInputDevId = (e) => {
        inputDevId(e.target.value);
    };
    const handleInputPass = (e) => {
        inputPassword(e.target.value);
    };
    // const callAPI  =()=>{
    //     setTimeout(callTheAPI, 1000);
    //     return 1;
    // }
    async function callAPI(){
        let res = await axios({
            url: 'http://localhost:8000/userAuth',
            method: 'post',
            timeout: 8000,
            headers: {
                'Content-Type': 'application/json',
            },
            data : { devId: devloperId,password:pass}
        })
        if(res.status == 200){
            // test for status you want, etc
            console.log(res)
        }    
        // Don't forget to return something   
        return res.data.status
        // await axios.post('http://localhost:8000/userAuth', { devId: devloperId,password:pass}).then(response => {
        // console.log(response["data"]["status"])    
        // if(response["data"]["status"]==="failure"){
        //         console.log("here")
        //         return "failure";
        //     }else{
        //         return "success";
        //     }
        // }).catch(error => {
        //   console.log(error);
        // });
      }
    const toggleButtonState = () => {
        fetchAPI().then(result => {
            console.log(result)
            setTicketData(result);
        });
    };
    function fetchAPI() {
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ devId: devloperId,password:pass}),
        };
        return fetch("http://localhost:8000/userAuth",requestOptions);
    }
    return (
        <>
            <form>
                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Developer Id</label>
                    <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" value={devloperId} onChange={handleInputDevId}/>
                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                    <input type="password" className="form-control" id="exampleInputPassword1" value={pass} onChange={handleInputPass} />
                </div>
                <button type="submit" className="btn btn-primary" onClick={()=>{let result;callAPI().then(res=>result=res);console.log(result)}}>Login</button>
            </form>
        </>
    )
}
