import React from 'react'
import {Ticket} from "./Ticket";
import { useEffect,useState } from 'react';
import {GetTicketDetail} from './GetTicketDetails.js'
export const AllTickets = (props) => {
    console.log("dvsdvsd")
    console.log([props]);
    const [renderAllTicket, setRenderAllTicketState] = useState(props.showAllTicket===true ? 'yes' : 'no')
    let s;
    useEffect(() => {
        if(props.showAllTicket===true){
            s = setInterval(() => {
                setRenderAllTicketState('yes');
              }, 50000);
        }else{
            console.log("noooooo")
        }

    }, [])
    console.log(renderAllTicket);
    const [individualTicketDetail, setTicketDetail] = useState(null);
    // if(props.showAllTicket==true){
    //     setRenderAllTicketState('yes')
    // }

    const triggershowIndividualTicket = (currentTicket) => {
        setTicketDetail(currentTicket)
        setRenderAllTicketState('individual')
    }
    let style={

    }
    return (
        <div>
            {console.log(renderAllTicket)}
        {renderAllTicket === 'yes' && (
        <div style={style} className="container">
            <h3 className="text-center my-3">All available ticket</h3>
            {props.allTheTickets.length===0? "No Ticket to display":  
            props.allTheTickets.map((allTickets)=>{
                //console.log(todo.sno);
                return <Ticket allTickets = {allTickets} key={allTickets.ticket_id} showIndividualTicket={triggershowIndividualTicket}/>
                //return (<TodoItem todo={todo} key={todo.sno} onDelete={props.onDelete}/>   

            })
              } 
        </div>)}
        {renderAllTicket === 'individual' && <GetTicketDetail TicketDetail={individualTicketDetail}/>}
        </div>
    )
}
